class Alumno {
    constructor(connection) {
        this.connection = connection;
    }

    getAll(callback) {
        this.connection.query('SELECT * FROM alumnos', callback);
    }

    create(data, callback) {
        this.connection.query('INSERT INTO alumnos SET ?', data, callback);
    }
    // Obtener estad√≠sticas de alumnos por edad
    getEstadisticasEdad(callback) {
        const query = `
            SELECT 
                YEAR(CURDATE()) - YEAR(fechaNacimiento) AS edad, 
                COUNT(*) AS total 
            FROM alumnos 
            GROUP BY edad
            ORDER BY edad;
        `;
        this.connection.query(query, callback);
    }
}

module.exports = Alumno;