const { Router } = require('express');

const connection = require('../../app').connection;

class Alumno {
    static getAll(callback) {
        connection.query('SELECT * FROM alumnos', callback);
    }

    static getById(id, callback) {
        connection.query('SELECT * FROM alumnos WHERE id = ?', [id], callback);
    }

    static create(data, callback) {
        connection.query('INSERT INTO alumnos SET ?', data, callback);
    }

    static update(id, data, callback) {
        connection.query('UPDATE alumnos SET ? WHERE id = ?', [data, id], callback);
    }

    static delete(id, callback) {
        connection.query('DELETE FROM alumnos WHERE id = ?', [id], callback);
    }

    static getEstadisticasEdad(callback) {
        connection.query('SELECT YEAR(CURDATE()) - YEAR(fechaNacimiento) AS edad, COUNT(*) AS total FROM alumnos GROUP BY edad', callback);
    }
}

module.exports = Alumno;
