const express = require('express');
const alumnoController = require('../controllers/alumnoController'); // Importa el controlador

const router = express.Router();

// Define las rutas y asocia las funciones del controlador
router.get('/', alumnoController.getAllAlumnos); // Usa la función getAllAlumnos
router.post('/', alumnoController.createAlumno); // Usa la función createAlumno
router.get('/estadisticas', alumnoController.getEstadisticasEdad); // Nueva ruta para estadísticas

module.exports = router;